mapper.select:
select r.RECORD_ID as STOCKIN_ID, r.RECORD_GOODID as STOCKIN_GOOD_ID, g.GOOD_NAME as STOCKIN_GOOD_NAME, r.RECORD_NUMBER as STOCKIN_NUMBER, r.RECORD_TIME as STOCKIN_TIME, r.RECORD_PERSON as STOCKIN_PERSON, r.RECORD_REPOSITORYID as STOCKIN_REPOSITORY_ID, t1.REPO_NAME as REPO_NAME from wms_record_in r,wms_goods g,wms_respository t1 where r.RECORD_GOODID = g.GOOD_ID and r.RECORD_REPOSITORYID = t1.REPO_ID
and r.RECORD_REPOSITORYID = #{repositoryId}
and r.RECORD_TIME >= #{startDate}
and r.RECORD_TIME <= #{endDate}
